// https://leetcode.com/problems/count-beautiful-substrings-ii/
#include <iostream>
#include <vector>
#include <algorithm>
#include <string>
#include <map>
#include <set>
#include <unordered_map>
#include <stack>
#include <numeric>
#include <queue>

using namespace std;

//#####
struct Window {
    int vowels;
    int cons;

    Window(int vowels, int cons) : vowels(vowels), cons(cons) {}

    Window() {
        vowels = 0;
        cons = 0;
    }
};

class Solution {
public:
    int beautifulSubstrings(string s, int k) {
        int ans = 0;
        int n = s.length();
        int step = 1;
        while (step * step % k != 0) {
            step++;
        }
        int windowUnit = 2 * step;
        int windowCount = n - windowUnit + 1; // how many unit windows in total
        int maxWidth = n / windowUnit;
        vector<Window> windows(windowCount);

        // count smaller ones
        int vowels = 0;
        int consonants = 0;
        // first window
        int i = 0;
        for (; i < windowUnit; i++) {
            if (isVowel(s[i])) {
                vowels++;
            } else {
                consonants++;
            }
        }
        windows[0] = Window(vowels, consonants);
        if (vowels == consonants) ans++;

        // sliding window
        for (; i < windows.size(); i++) {
            if ((isVowel(s[i]) && isVowel(s[i - windowUnit])) ||
                (!isVowel(s[i]) && !isVowel(s[i - windowUnit]))) {
                ans++;
            } else if (isVowel(s[i])) {
                vowels++;
                consonants--;
            } else {
                vowels--;
                consonants++;
            }
            windows[i] = Window(vowels, consonants);
        }

        // merge into bigger windows
        for (int width = 2; width <= maxWidth; width++) {
            // first window
            vowels = 0;
            consonants = 0;
            i = 0;
            int currentSize = width * windowUnit;
            for(; i < width; i++) {
                vowels += windows[0+i*windowUnit].vowels;
                consonants += windows[0+i*windowUnit].cons;
            }
            if (vowels == consonants) ans++;

            // sliding window
            for(; i < windows.size(); i++) {
                vowels = vowels - windows[i-currentSize].vowels + windows[i].vowels;
                consonants = consonants - windows[i-currentSize].cons+ windows[i].cons;
                if (vowels == consonants) ans++;
            }
        }
        return ans;
    }

    bool isVowel(char ch) {
        return ch == 'a' || ch == 'e' || ch == 'i' || ch == 'o' || ch == 'u';
    }
};
//#####

// do not copy lines after this to LeetCode
int main() {
    Solution sol;
    int ans;
    string s;
    int k;

    s = "baeyh", k = 2; // 2
    s = "abba", k = 1; // 3
    s = "bcdf", k = 1;
    k = 2; // 312487500
    s = "";
    ans = sol.beautifulSubstrings(s, k);
    std::cout << ans << std::endl;
}
        