// https://leetcode.com/problems/count-beautiful-substrings-i/
#include <iostream>
#include <vector>
#include <algorithm>
#include <string>
#include <map>
#include <set>
#include <unordered_map>
#include <stack>
#include <numeric>
#include <queue>

using namespace std;

//#####
class Solution {
public:
    int beautifulSubstrings(string s, int k) {
        // the string must have nk vowels, and nk consonants, so it must be longer than 2n*k, n=1.... len/2k
        // so use sliding window of size 2k, 4k, 8k

        int ans = 0;
        int n = 1;
        while (n * n % k != 0) {
            n++;
        }

        int step = 2 * n;

        for (int window = step; window <= s.length(); window += step) {
            int vowels = 0;
            int consonants = 0;
            // init window
            int i = 0;
            for (; i < window; i++) {
                if (isVowel(s[i])) {
                    vowels++;
                } else {
                    consonants++;
                }
            }
            if (vowels == consonants) ans++;

            for (; i < s.length(); i++) {
                if (isVowel(s[i - window])) { // a vowel fall off window
                    vowels--;
                } else {
                    consonants--;
                }
                // add new one to window
                if (isVowel(s[i])) {
                    vowels++;
                } else {
                    consonants++;
                }
                if (vowels == consonants) ans++;
            }

        }

        return ans;
    }

    bool isVowel(char ch) {
        return ch == 'a' || ch == 'e' || ch == 'i' || ch == 'o' || ch == 'u';
    }
};
//#####

// do not copy lines after this to LeetCode
int main() {
    Solution sol;
    int ans;
    string s;
    int k;

    s = "baeyh", k = 2; // 2
//    s = "abba", k = 1; // 3
//    s = "";
//    k = 2; // 312487500
    ans = sol.beautifulSubstrings(s, k);
    std::cout << ans << std::endl;
}
        